			<script>
(function($) {
	
	function bgLoaded($bg) {
		
		$bg.addClass('bg-loaded');
		
		setTimeout(function(){
			$bg.removeClass('will-change');
		}, 3000);
		
		if ( $bg.hasClass('use-parallax') ) {
		
			var $bgWrapper = $bg.parent();
			
			$bgWrapper.addClass('will-change');
							
			UIkit.parallax($bgWrapper, {
				y: '240px'
			});
			
		}
		
		jQuery('#hero_text_1').each(function(){
		
			var $heroText = jQuery(this);
			
			var $heroItems = $heroText.find('h1, .intro-action > a');
			
			$heroItems.addClass('will-change');
			
			var textViewport = ( $bg.hasClass('hero-bg-mobile') && $bg.hasClass('home-hero-bg') ) ? '.9' : '1';
		
			UIkit.parallax($heroText, {
				opacity: '1,0',
				viewport: textViewport
			});
			
			$heroText.addClass('parallaxed');
			
			// Remove after intro_title animation delay + duration
			setTimeout(function(){
				$heroItems.removeClass('will-change');
			}, 3500);

		});
		
		jQuery('#hero_text_2').each(function(){
		
			var $heroText = jQuery(this);
			
			$heroText.addClass('will-change');
		
			UIkit.parallax($heroText, {
				opacity: '1,0',
				viewport: '0.95'
			});

			$heroText.addClass('parallaxed');
			
		});
		
		
	}
	
	function loadBGs() {
		
		jQuery('#hero_bg_desktop, #hero_bg_mobile').each(function(){
			
			var $bg = jQuery(this);
			
			if ( $bg.css('display') === 'none' || $bg.data('lazyLoadComplete') === true ) {
				return;
			}
			
			var lazySrc = $bg.attr('data-lazybg');
			
			if ( lazySrc ) {
				
				var loadCompleted = false;
				
				$bg.addClass('will-change').data('lazyLoadComplete', true);
				
				var image = new Image();
				
				image.onabort = function() {
					console.error('Background image load failed.');
					bgLoaded($bg);
					loadCompleted = true;
				};
				image.onerror = function() {
					console.error('Background image failed to load.');
					bgLoaded($bg);
					loadCompleted = true;
				};
				image.onload = function() {
					bgLoaded($bg);
					loadCompleted = true;
				};

				// Must encode this here too, otherwise there may be discrepancies between Photon query string and preload URL
				image.src = encodeURI(lazySrc);
				
				$bg.css('background-image', 'url(' + encodeURI(lazySrc) + ')');
				
				setTimeout(function(){
					if ( ! loadCompleted ) {
						bgLoaded($bg);
					}
				}, 2000);

			}
		});
		
	}
	
	loadBGs();
	
	$(window).on('debouncedresize', loadBGs);
	
	$(document.body).on('post-load', loadBGs);
	
})(jQuery);
</script>	
